# -*- coding: utf-8 -*-
"""
termcolor's documentation settings
"""
from __future__ import unicode_literals


# project information
project = 'termcolor'
copyright = '2013, Igor Kalnitsky'
version = '0.1'
release = '0.1'

# sphinx settings
extensions = []
